 <section id="contact" class="bg-blue relative bg-white:bg-gray-900 flex z-0" id="contact" aria-labelledby="contact-heading">
    <div class="flex-none w-2/5 relative image-container">
            <div class="absolute flex items-center justify-center w-full h-full">
                <img src="../../../../../assets/demo/images/landing/contact.png" alt="" class="max-w-full max-h-full contact-image">
            </div>
        </div>
        <div class="flex-auto py-8 lg:py-16 px-4 mx-auto max-w-screen-md relative z-10">
            <h2 id="contact-heading" class="mb-4 text-4xl tracking-tight font-extrabold text-center text-gray-300 dark:text-white">Contactez-nous</h2>
            <p class="mb-8 lg:mb-16 font-light text-center text-gray-500 dark:text-gray-400 sm:text-xl">Souhaitez-vous nous faire part de vos commentaires ? Avez-vous besoin de détails sur notr virtual-Iteam platforme ? Faites-le nous savoir.</p>
            <form [formGroup]="emailForm" (ngSubmit)="sendEmail()">
                <div>
                    <label for="email" class="block mb-2 text-sm font-medium text-gray-500 dark:text-gray-300">Votre adresse e-mail</label>
                    <input type="email" id="email" formControlName="email"  name="email" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-500 text-sm rounded-lg focus:ring-primary-500 focus:border-primary-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500 dark:shadow-sm-light" placeholder="nom@medin.com" required>
                </div>
                <div>
                    <label for="subject" class="block mb-2 text-sm font-medium text-gray-500 dark:text-gray-400">Sujet</label>

                    <input type="text" id="subject" formControlName="subject"  name="subject" class="block p-3 w-full text-sm text-gray-500 bg-gray-50 rounded-lg border border-gray-300 shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500 dark:shadow-sm-light" placeholder="Dites-nous comment nous pouvons vous aider" required>
                  </div>
                  <div class="sm:col-span-2"> 
                    <label for="message" class="block mb-2 text-sm font-medium text-gray-500 dark:text-gray-400">Votre message</label>
                    <textarea id="message" rows="6"  formControlName="message"  name="message" class="block p-2.5 w-full text-sm text-gray-500 bg-gray-50 rounded-lg shadow-sm border border-gray-300 focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500"></textarea>
                  </div>
                <button type="submit" class="my-2 py-3 px-5 text-sm font-medium text-center text-white rounded-lg bg-primary-700 sm:w-fit hover:bg-primary-800 focus:ring-4 focus:outline-none focus:ring-primary-300 dark:bg-primary-600 dark:hover:bg-primary-700 dark:focus:ring-primary-800">Envoyer le message</button>
            </form>
        </div>
    </section>





