import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from 'src/app/demo/service/auth-student.service';
import { AuthTeacherService } from 'src/app/demo/service/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.scss']
})
export class AuthDialogComponent implements OnInit {

  isTeacher: boolean = false; // Default to student role
    studentLoginForm: FormGroup;
    teacherLoginForm: FormGroup;
    studentRegisterForm: FormGroup;
    teacherRegisterForm: FormGroup;

    constructor(private formBuilder: FormBuilder) { }

    ngOnInit(): void {
      // Initialize form groups
      this.studentLoginForm = this.formBuilder.group({
        email: ['', [Validators.required, Validators.email]],
        password: ['', Validators.required]
      });

      this.teacherLoginForm = this.formBuilder.group({
        email: ['', [Validators.required, Validators.email]],
        password: ['', Validators.required]
      });

      this.studentRegisterForm = this.formBuilder.group({
        name: ['', Validators.required],
        lastname: ['', Validators.required],
        email: ['', [Validators.required, Validators.email]],
        password: ['', Validators.required]
      });

      this.teacherRegisterForm = this.formBuilder.group({
        name: ['', Validators.required],
        lastname: ['', Validators.required],
        email: ['', [Validators.required, Validators.email]],
        password: ['', Validators.required],
        specialization: ['', Validators.required]
      });
    }
    toggleRole(isTeacher: boolean) {
      this.isTeacher = isTeacher;
    }

    studentLogin() {
      // Implement student login logic here
    }

    teacherLogin() {
      // Implement teacher login logic here
    }

    studentRegister() {
      // Implement student registration logic here
    }

    teacherRegister() {
      // Implement teacher registration logic here
    }
  }

<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <!-- Tab selection for login or register -->
  <div class="flex justify-center mb-4">
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isLogin" (click)="toggleTab(true)">Login</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isLogin" (click)="toggleTab(false)">Register</button>
  </div>

  <!-- Login or register forms -->
  <div *ngIf="isLogin">
    <!-- Login Form -->
    <h2 class="text-2xl font-bold mb-4">Login</h2>
    <form [formGroup]="loginForm" (ngSubmit)="login()">
      <!-- Login Form Fields -->
    </form>
  </div>
  <div *ngIf="!isLogin">
    <!-- Registration Form -->
    <h2 class="text-2xl font-bold mb-4">Register</h2>
    <!-- Toggle between student and teacher registration -->
    <div class="flex justify-center mb-4">
      <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isTeacher" (click)="toggleRole(false)">Student</button>
      <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isTeacher" (click)="toggleRole(true)">Teacher</button>
    </div>
    <!-- Registration Form (Student) -->
    <div *ngIf="!isTeacher">
      <form [formGroup]="studentRegisterForm" (ngSubmit)="register()">
        <!-- Student Registration Form Fields -->
      </form>
    </div>
    <!-- Registration Form (Teacher) -->
    <div *ngIf="isTeacher">
      <form [formGroup]="teacherRegisterForm" (ngSubmit)="register()">
        <!-- Teacher Registration Form Fields -->
      </form>
    </div>
  </div>
</div>
