import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from '../services/auth-student.service';
import { AuthTeacherService } from '../services/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.css']
})
export class AuthDialogComponent implements OnInit {
  isLogin: boolean = true; // Default to login mode
  studentLoginForm: FormGroup;
  teacherLoginForm: FormGroup;
  studentRegisterForm: FormGroup;
  teacherRegisterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authStudentService: AuthStudentService,
    private authTeacherService: AuthTeacherService
  ) {}

  ngOnInit(): void {
    this.studentLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.studentRegisterForm = this.fb.group({
      // Student Register Form Controls
    });

    this.teacherRegisterForm = this.fb.group({
      // Teacher Register Form Controls
    });
  }

  toggleLogin() {
    this.isLogin = !this.isLogin;
  }

  studentLogin() {
    if (this.studentLoginForm.valid) {
      this.authStudentService.login(this.studentLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherLogin() {
    if (this.teacherLoginForm.valid) {
      this.authTeacherService.login(this.teacherLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  studentRegister() {
    if (this.studentRegisterForm.valid) {
      this.authStudentService.register(this.studentRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherRegister() {
    if (this.teacherRegisterForm.valid) {
      this.authTeacherService.register(this.teacherRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }
}
