import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from 'src/app/demo/service/auth-student.service';
import { AuthTeacherService } from 'src/app/demo/service/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.scss']
})
export class AuthDialogComponent implements OnInit {
  isLogin: boolean = true; // Default to login mode
  isStudent: boolean = true; // Default to student mode

  studentLoginForm: FormGroup;
  teacherLoginForm: FormGroup;
  studentRegisterForm: FormGroup;
  teacherRegisterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authStudentService: AuthStudentService,
    private authTeacherService: AuthTeacherService
  ) {}

  ngOnInit(): void {
    this.studentLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.studentRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      specialization: [''] // Optional field
    });
  }

  toggleLogin() {
    this.isLogin = !this.isLogin;
  }

  toggleStudent() {
    this.isStudent = true;
  }

  toggleTeacher() {
    this.isStudent = false;
  }

  studentLogin() {
    if (this.studentLoginForm.valid) {
      this.authStudentService.studentLogin(this.studentLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherLogin() {
    if (this.teacherLoginForm.valid) {
      this.authTeacherService.teacherLogin(this.teacherLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  studentRegister() {
    if (this.studentRegisterForm.valid) {
      this.authStudentService.studentRegister(this.studentRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherRegister() {
    if (this.teacherRegisterForm.valid) {
      this.authTeacherService.teacherRegister(this.teacherRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }
}
<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <!-- Switch between Login/Register -->
  <div class="flex justify-center mb-4">
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isLogin" (click)="toggleLogin()">Login</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isLogin" (click)="toggleLogin()">Register</button>
  </div>

  <!-- Switch between Student/Teacher -->
  <div *ngIf="isLogin || !isLogin">
    <div class="flex justify-center mb-4">
      <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isStudent" (click)="toggleStudent()">Student</button>
      <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isStudent" (click)="toggleTeacher()">Teacher</button>
    </div>

    <!-- Login Form -->
    <div *ngIf="isLogin && (isStudent || isTeacher)">
      <h2 class="text-2xl font-bold mb-4">{{ isStudent ? 'Student' : 'Teacher' }} Login</h2>
      <form [formGroup]="isStudent ? studentLoginForm : teacherLoginForm" (ngSubmit)="isStudent ? studentLogin() : teacherLogin()">
        <!-- Login Form Fields -->
        <input type="email" formControlName="email" placeholder="Email">
        <input type="password" formControlName="password" placeholder="Password">
        <button type="submit">Login</button>
      </form>
    </div>

    <!-- Register Form -->
    <div *ngIf="!isLogin && (isStudent || isTeacher)">
      <h2 class="text-2xl font-bold mb-4">{{ isStudent ? 'Student' : 'Teacher' }} Register</h2>
      <form [formGroup]="isStudent ? studentRegisterForm : teacherRegisterForm" (ngSubmit)="isStudent ? studentRegister() : teacherRegister()">
        <!-- Register Form Fields -->
        <input type="text" formControlName="name" placeholder="Name">
        <input type="text" formControlName="lastname" placeholder="Lastname">
        <input type="email" formControlName="email" placeholder="Email">
        <input type="password" formControlName="password" placeholder="Password">
        <input type="text" formControlName="specialization" *ngIf="!isStudent" placeholder="Specialization">
        <button type="submit">Register</button>
      </form>
    </div>
  </div>
</div>
