// team-member.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { TeamMember } from '../api/teamMember';

@Injectable({
  providedIn: 'root',
})
export class TeamMemberService {
  private apiUrl = 'http://localhost:3002/api/team'; // Update the URL accordingly

  constructor(private http: HttpClient) {}

  getTeamMembers(): Observable<TeamMember[]> {
    return this.http.get<TeamMember[]>(this.apiUrl);
  }

  addTeamMember(teamMember: TeamMember): Observable<TeamMember> {
    // You might want to remove the image property before sending it to the server
    const { image, ...dataWithoutImage } = teamMember;
    
    return this.http.post<TeamMember>(this.apiUrl, dataWithoutImage);
  }

  updateTeamMember(id: string, teamMember: TeamMember): Observable<TeamMember> {
    // You might want to remove the image property before sending it to the server
    const { image, ...dataWithoutImage } = teamMember;

    const url = `${this.apiUrl}/${id}`;
    return this.http.put<TeamMember>(url, dataWithoutImage);
  }

  deleteTeamMember(id: string): Observable<void> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.delete<void>(url);
  }

  
}
