import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthAdminService } from 'src/app/demo/service/auth-admin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  rememberMe: boolean = false;

  constructor(private authService: AuthAdminService, private router: Router) {}

  login() {
    this.authService.login({ email: this.email, password: this.password }).subscribe((response: any) => {
      // Assuming the response contains a token upon successful login
      const token = response.token;
      // Store token in local storage or session storage for future requests
      localStorage.setItem('token', token);
      // Redirect to home page or any other desired route
      this.router.navigate(['/']);
    }, (error: any) => {
      // Handle login error
      console.error(error);
      // Optionally display error message to user
    });
  }
}
<div class="surface-ground flex align-items-center justify-content-center min-h-screen min-w-screen overflow-hidden">
    <div class="flex flex-column align-items-center justify-content-center">
        <div style="border-radius:56px; padding:0.3rem; background: linear-gradient(180deg, var(--primary-color) 10%, rgba(33, 150, 243, 0) 30%);">
            <div class="w-full surface-card py-8 px-5 sm:px-8" style="border-radius:53px">
                <div class="text-center mb-5">
                    <img src="assets/demo/images/login/avatar.png" alt="Image" height="50" class="mb-3">
                    <div class="text-900 text-3xl font-medium mb-3">Welcome, Isabel!</div>
                    <span class="text-600 font-medium">Sign in to continue</span>
                </div>

                <div>
                    <form [formGroup]="loginForm" (ngSubmit)="login()">
                        <label for="email1" class="block text-900 text-xl font-medium mb-2">Email</label>
                        <input id="email1" type="text" placeholder="Email address" formControlName="email" class="w-full md:w-30rem mb-5" style="padding:1rem">

                        <label for="password1" class="block text-900 font-medium text-xl mb-2">Password</label>
                        <input id="password1" type="password" placeholder="Password" formControlName="password" class="w-full p-3 md:w-30rem mb-5">

                        <div class="flex align-items-center justify-content-between mb-5 gap-5">
                            <div class="flex align-items-center">
                                <input id="rememberme1" type="checkbox" [(ngModel)]="rememberMe" class="mr-2">
                                <label for="rememberme1">Remember me</label>
                            </div>
                            <a href="#" class="font-medium no-underline ml-2 text-right" style="color: var(--primary-color)">Forgot password?</a>
                        </div>
                        <button type="submit" pButton pRipple label="Sign In" class="w-full p-3 text-xl"></button>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
