import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from './auth.service';
import { AuthAdminService } from 'src/app/demo/service/auth-admin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  rememberMe: boolean = false;

  constructor(private authService: AuthAdminService, private router: Router) {}

  login() {
    this.authService.login({ email: this.email, password: this.password }}.subscribe((response: any) => {
      // Assuming the response contains a token upon successful login
      const token = response.token;
      // Store token in local storage or session storage for future requests
      localStorage.setItem('token', token);
      // Redirect to home page or any other desired route
      this.router.navigate(['/']);
    }, (error: any) => {
      // Handle login error
      console.error(error);
      // Optionally display error message to user
    });
  }
}
