<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <!-- Panel to choose between Login and Register -->
  <div class="flex justify-center mb-4">
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none"
            [class.bg-gray-200]="isLogin" (click)="toggleLogin()">Login</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none"
            [class.bg-gray-200]="!isLogin" (click)="toggleLogin()">Register</button>
  </div>

  <!-- Form for Register -->
  <div *ngIf="!isLogin">
    <h2 class="text-2xl font-bold mb-4">Register</h2>
    <form [formGroup]="registerForm" (ngSubmit)="register()">
      <!-- Common Fields -->
      <div class="mb-4">
        <label for="name" class="block text-gray-700">Name</label>
        <input type="text" id="name" formControlName="name"
               class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="lastname" class="block text-gray-700">Lastname</label>
        <input type="text" id="lastname" formControlName="lastname"
               class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="registerEmail" class="block text-gray-700">Email</label>
        <input type="email" id="registerEmail" formControlName="email"
               class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
        <div *ngIf="registerForm.get('email').invalid && (registerForm.get('email').dirty || registerForm.get('email').touched)"
             class="text-red-500 text-sm mt-1">Email is required and must be valid</div>
      </div>
      <div class="mb-4">
        <label for="registerPassword" class="block text-gray-700">Password</label>
        <input type="password" id="registerPassword" formControlName="password"
               class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
        <div *ngIf="registerForm.get('password').invalid && (registerForm.get('password').dirty || registerForm.get('password').touched)"
             class="text-red-500 text-sm mt-1">Password is required</div>
      </div>

      <!-- Additional Fields for Student -->
      <div *ngIf="registerForm.get('userType').value === 'student'">
        <div class="mb-4">
          <label for="studentId" class="block text-gray-700">Student ID</label>
          <input type="text" id="studentId" formControlName="studentId"
                 class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
        </div>
      </div>

      <!-- Additional Fields for Teacher -->
      <div *ngIf="registerForm.get('userType').value === 'teacher'">
        <div class="mb-4">
          <label for="specialization" class="block text-gray-700">Specialization</label>
          <input type="text" id="specialization" formControlName="specialization"
                 class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
        </div>
        <div class="mb-4">
          <label for="experience" class="block text-gray-700">Experience</label>
          <input type="text" id="experience" formControlName="experience"
                 class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
        </div>
      </div>

      <!-- User Type Select -->
      <div class="mb-4">
        <label class="block text-gray-700">User Type</label>
        <div class="flex items-center mt-2">
          <input type="radio" id="student" name="userType" value="student" formControlName="userType" checked
                 class="mr-2">
          <label for="student" class="mr-4">Student</label>
          <input type="radio" id="teacher" name="userType" value="teacher" formControlName="userType"
                 class="mr-2">
          <label for="teacher">Teacher</label>
        </div>
      </div>

      <!-- Submit Button -->
      <button type="submit"
              class="w-full bg-indigo-500 text-white font-bold py-2 px-4 rounded hover:bg-indigo-700 focus:outline-none focus:shadow-outline">
        Register
      </button>
    </form>
  </div>
</div>
