import { Component, HostListener } from '@angular/core';
import { Router } from '@angular/router';
import { LayoutService } from 'src/app/layout/service/app.layout.service';
import { trigger, state, style, animate, transition } from '@angular/animations';
const fadeStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const fadeStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const leftToRightStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const leftToRightStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const fadeDownStyle1 = style({
  opacity: 1,
  transform: 'translateY(0)'
});

const fadeDownStyle2 = style({
  opacity: 0,
  transform: 'translateY(-100%)'
});
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls:['./landing.component.scss'],
  animations: [
    trigger('fade', [
      state('show', fadeStyle1),
      state('hide', fadeStyle2),
      transition('show => hide', animate('700ms ease-out')),
      transition('hide => show', animate('700ms ease-in'))
    ]),
    trigger('leftToRight', [
      state('show', leftToRightStyle1),
      state('hide', leftToRightStyle2),
      transition('show => hide', animate('700ms ease-out')),
      transition('hide => show', animate('700ms ease-in'))
    ]),
    trigger('fadeDown', [
      state('show', fadeDownStyle1),
      state('hide', fadeDownStyle2),
      transition('show => hide', animate('700ms ease-out')),
      transition('hide => show', animate('700ms ease-in'))
    ])
  ]
})
export class LandingComponent {
  isNavbarGray = false;
  isResponsiveMenuGray: boolean = false;
  fadeState = 'hide';
  leftToRightState = 'hide';
  fadeDownState = 'hide';
  constructor(public layoutService: LayoutService, public router: Router,public el: ElementRef) { }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    // Add a scroll event listener to toggle the class based on the scroll position
    this.isNavbarGray = window.scrollY > 100; // Adjust the scroll position threshold
  }
  toggleNavbarColor() {
    this.isNavbarGray = true;
}
@HostListener('window:scroll', ['$event'])
checkScroll() {
  const componentPosition = this.el.nativeElement.offsetTop;

  // For the 'fade' animation
  if (window.pageYOffset >= componentPosition - 250) {
    this.fadeState = 'show';
  } else {
    this.fadeState = 'hide';
  }

  // For the 'leftToRight' animation
  if (window.pageYOffset >= componentPosition - 250) {
    this.leftToRightState = 'show';
  } else {
    this.leftToRightState = 'hide';
  }

  // For the 'fadeDown' animation
  if (window.pageYOffset >= componentPosition - 250) {
    this.fadeDownState = 'show';
  } else {
    this.fadeDownState = 'hide';
  }
}

}
