// blog.component.ts
import { Component, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
iimport { QuillService } from 'ngx-quill';
import Quill from 'quill';
@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
})
export class BlogComponent implements AfterViewInit {
  @ViewChild('quillEditor') quillEditor!: ElementRef;
  blogMember = { content: '' }; // Assuming you have a blogMember object

  constructor(private quillService: QuillService) {}

  ngAfterViewInit() {
    const quill = this.quillService.getQuill();
    const editor = new Quill(this.quillEditor.nativeElement, {
      theme: 'snow',
      modules: {
        toolbar: [
          ['bold', 'italic', 'underline', 'strike'],
          ['blockquote', 'code-block'],
          [{ 'header': 1 }, { 'header': 2 }],
          [{ 'list': 'ordered' }, { 'list': 'bullet' }],
          [{ 'script': 'sub' }, { 'script': 'super' }],
          [{ 'indent': '-1' }, { 'indent': '+1' }],
          [{ 'direction': 'rtl' }],
          [{ 'size': ['small', false, 'large', 'huge'] }],
          [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
          [{ 'color': [] }, { 'background': [] }],
          [{ 'font': [] }],
          [{ 'align': [] }],
          ['clean'],
        ],
      },
    });

    editor.on('text-change', () => {
      this.blogMember.content = editor.root.innerHTML;
    });
  }
}
