import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from 'src/app/demo/service/auth-student.service';
import { AuthTeacherService } from 'src/app/demo/service/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.scss']
})
export class AuthDialogComponent implements OnInit {
  isLogin: boolean = true; // Default to login mode
  isStudent: boolean = true; // Default to student mode  studentLoginForm: FormGroup;
  teacherLoginForm: FormGroup;
  studentRegisterForm: FormGroup;
  teacherRegisterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authStudentService: AuthStudentService,
    private authTeacherService: AuthTeacherService
  ) {}

  ngOnInit(): void {
    this.studentLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.studentRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      specialization: [''] // Optional field
    });

  }

  toggleLogin() {
    this.isLogin = !this.isLogin;
  }

  studentLogin() {
    if (this.studentLoginForm.valid) {
      this.authStudentService.studentLogin(this.studentLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherLogin() {
    if (this.teacherLoginForm.valid) {
      this.authTeacherService.teacherLogin(this.teacherLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  studentRegister() {
    if (this.studentRegisterForm.valid) {
      this.authStudentService.studentRegister(this.studentRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherRegister() {
    if (this.teacherRegisterForm.valid) {
      this.authTeacherService.teacherRegister(this.teacherRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }
}
<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <!-- Login/Register Buttons -->
  <div class="flex justify-center mb-4">
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isLogin && isStudent" (click)="toggleLogin(true)">Student Login</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isLogin && !isStudent" (click)="toggleLogin(false)">Teacher Login</button>
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isLogin && isStudent" (click)="toggleLogin(true)">Student Register</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="!isLogin && !isStudent" (click)="toggleLogin(false)">Teacher Register</button>
  </div>

  <!-- Login Form -->
  <div *ngIf="isLogin">
    <h2 class="text-2xl font-bold mb-4">{{ isStudent ? 'Student Login' : 'Teacher Login' }}</h2>
    <form [formGroup]="isStudent ? studentLoginForm : teacherLoginForm" (ngSubmit)="isStudent ? studentLogin() : teacherLogin()">
      <div class="mb-4">
        <label for="loginEmail" class="block text-gray-700">Email</label>
        <input type="email" id="loginEmail" formControlName="email" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="loginPassword" class="block text-gray-700">Password</label>
        <input type="password" id="loginPassword" formControlName="password" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <button type="submit" class="w-full bg-indigo-500 text-white font-bold py-2 px-4 rounded hover:bg-indigo-700 focus:outline-none focus:shadow-outline">{{ isStudent ? 'Student Login' : 'Teacher Login' }}</button>
    </form>
  </div>

  <!-- Registration Form -->
  <div *ngIf="!isLogin">
    <h2 class="text-2xl font-bold mb-4">{{ isStudent ? 'Student Register' : 'Teacher Register' }}</h2>
    <form [formGroup]="isStudent ? studentRegisterForm : teacherRegisterForm" (ngSubmit)="isStudent ? studentRegister() : teacherRegister()">
      <div class="mb-4">
        <label for="registerName" class="block text-gray-700">Name</label>
        <input type="text" id="registerName" formControlName="name" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="registerLastname" class="block text-gray-700">Lastname</label>
        <input type="text" id="registerLastname" formControlName="lastname" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="registerEmail" class="block text-gray-700">Email</label>
        <input type="email" id="registerEmail" formControlName="email" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="registerPassword" class="block text-gray-700">Password</label>
        <input type="password" id="registerPassword" formControlName="password" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <button type="submit" class="w-full bg-indigo-500 text-white font-bold py-2 px-4 rounded hover:bg-indigo-700 focus:outline-none focus:shadow-outline">{{ isStudent ? 'Student Register' : 'Teacher Register' }}</button>
    </form>
  </div>
</div>
