import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from 'src/app/demo/service/auth-student.service';
import { AuthTeacherService } from 'src/app/demo/service/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.scss']
})
export class AuthDialogComponent implements OnInit {
  isStudentLogin: boolean = true;
  isTeacherLogin: boolean = false;
  isStudentRegister: boolean = true;
  isTeacherRegister: boolean = false;  
  studentLoginForm: FormGroup;
  teacherLoginForm: FormGroup;
  studentRegisterForm: FormGroup;
  teacherRegisterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authStudentService: AuthStudentService,
    private authTeacherService: AuthTeacherService
  ) {}

  ngOnInit(): void {
    this.studentLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.studentRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      specialization: [''] // Optional field
    });

  }


  toggleStudentLogin() {
    this.isStudentLogin = true;
    this.isTeacherLogin = false;
  }

  toggleTeacherLogin() {
    this.isStudentLogin = false;
    this.isTeacherLogin = true;
  }

  toggleStudentRegister() {
    this.isStudentRegister = true;
    this.isTeacherRegister = false;
  }

  toggleTeacherRegister() {
    this.isStudentRegister = false;
    this.isTeacherRegister = true;
  }
  studentLogin() {
    if (this.studentLoginForm.valid) {
      this.authStudentService.studentLogin(this.studentLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherLogin() {
    if (this.teacherLoginForm.valid) {
      this.authTeacherService.teacherLogin(this.teacherLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  studentRegister() {
    if (this.studentRegisterForm.valid) {
      this.authStudentService.studentRegister(this.studentRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherRegister() {
    if (this.teacherRegisterForm.valid) {
      this.authTeacherService.teacherRegister(this.teacherRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }
}
<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <div class="flex justify-center mb-4">
    <button class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isStudentLogin" (click)="toggleStudentLogin()">Student</button>
    <button class="py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="isTeacherLogin" (click)="toggleTeacherLogin()">Teacher</button>
  </div>

  <!-- Login Form -->
  <div *ngIf="isStudentLogin || isTeacherLogin">
    <h2 class="text-2xl font-bold mb-4">{{ isStudentLogin ? 'Student' : 'Teacher' }} Login</h2>
    <form [formGroup]="isStudentLogin ? studentLoginForm : teacherLoginForm" (ngSubmit)="isStudentLogin ? studentLogin() : teacherLogin()">
      <div class="mb-4">
        <label for="{{ isStudentLogin ? 'student' : 'teacher' }}LoginEmail" class="block text-gray-700">Email</label>
        <input type="email" id="{{ isStudentLogin ? 'student' : 'teacher' }}LoginEmail" formControlName="email" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="{{ isStudentLogin ? 'student' : 'teacher' }}LoginPassword" class="block text-gray-700">Password</label>
        <input type="password" id="{{ isStudentLogin ? 'student' : 'teacher' }}LoginPassword" formControlName="password" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <button type="submit" class="w-full bg-indigo-500 text-white font-bold py-2 px-4 rounded hover:bg-indigo-700 focus:outline-none focus:shadow-outline">Login</button>
    </form>
  </div>

  <!-- Register Form -->
  <div *ngIf="isStudentRegister || isTeacherRegister">
    <h2 class="text-2xl font-bold mb-4">{{ isStudentRegister ? 'Student' : 'Teacher' }} Register</h2>
    <form [formGroup]="isStudentRegister ? studentRegisterForm : teacherRegisterForm" (ngSubmit)="isStudentRegister ? studentRegister() : teacherRegister()">
      <!-- Register Form Fields -->
      <div class="mb-4">
        <label for="{{ isStudentRegister ? 'student' : 'teacher' }}Name" class="block text-gray-700">Name</label>
        <input type="text" id="{{ isStudentRegister ? 'student' : 'teacher' }}Name" formControlName="name" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="{{ isStudentRegister ? 'student' : 'teacher' }}Lastname" class="block text-gray-700">Lastname</label>
        <input type="text" id="{{ isStudentRegister ? 'student' : 'teacher' }}Lastname" formControlName="lastname" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="{{ isStudentRegister ? 'student' : 'teacher' }}Email" class="block text-gray-700">Email</label>
        <input type="email" id="{{ isStudentRegister ? 'student' : 'teacher' }}Email" formControlName="email" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <div class="mb-4">
        <label for="{{ isStudentRegister ? 'student' : 'teacher' }}Password" class="block text-gray-700">Password</label>
        <input type="password" id="{{ isStudentRegister ? 'student' : 'teacher' }}Password" formControlName="password" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <!-- Additional fields for teacher registration -->
      <div *ngIf="isTeacherRegister" class="mb-4">
        <label for="teacherSpecialization" class="block text-gray-700">Specialization</label>
        <input type="text" id="teacherSpecialization" formControlName="specialization" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50">
      </div>
      <button type="submit" class="w-full bg-indigo-500 text-white font-bold py-2 px-4 rounded hover:bg-indigo-700 focus:outline-none focus:shadow-outline">Register</button>
    </form>
  </div>
</div>
