// auth-dialog.component.ts

import { Component, OnInit } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog'; // Import MatDialogRef to control the dialog

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.css']
})
export class AuthDialogComponent implements OnInit {

  email: string = '';
  password: string = '';

  constructor(public dialogRef: MatDialogRef<AuthDialogComponent>) { }

  ngOnInit(): void {
  }

  loginOrRegister(): void {
    // Implement your login/register logic here
    console.log("Email:", this.email);
    console.log("Password:", this.password);
    // You can close the dialog after authentication is successful
    this.dialogRef.close();
  }

}
