import { Component, HostListener, Renderer2, ElementRef } from '@angular/core';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.scss']
})
export class LandingComponent {
  isShrunk: boolean = false;

  constructor(public layoutService: LayoutService,private renderer: Renderer2, private el: ElementRef) { }

  @HostListener('window:scroll', ['$event'])
  onScroll() {
    // Update isShrunk based on the scroll position
    this.isShrunk = window.scrollY > 80;

    // Apply styles dynamically using Renderer2
    if (this.isShrunk) {
      this.renderer.setStyle(this.el.nativeElement, 'padding', '10px 10px');
      this.renderer.setStyle(this.el.nativeElement, 'background-color', '#f1f1f1');
    } else {
      this.renderer.removeStyle(this.el.nativeElement, 'padding');
      this.renderer.removeStyle(this.el.nativeElement, 'background-color');
    }
  }
}
