import { Component, OnInit } from '@angular/core';
import { TeamMember } from 'src/app/demo/api/teamMember';
import { MessageService } from 'primeng/api';
import { Table } from 'primeng/table';
import { TeamMemberService } from 'src/app/demo/service/TeamMember.Service';
import { ImageService } from 'src/app/demo/service/image.service';

@Component({
  templateUrl: './team.component.html',
  providers: [MessageService, TeamMemberService],
})
export class TeamComponent implements OnInit {
  teamMembers: TeamMember[] = [];
  teamMember: TeamMember | null = null;
  teamMemberDialog: boolean = false;
  deleteTeamMemberDialog: boolean = false;
  deleteTeamMembersDialog: boolean = false;
  selectedTeamMembers: TeamMember[] = [];
  submitted: boolean = false;

  cols: any[] = [
    { field: 'name', header: 'Name' },
    { field: 'position', header: 'Position' },
    { field: 'image', header: 'Image' },
  ];

  constructor(private teamMemberService: TeamMemberService, private messageService: MessageService ,private imageService:ImageService) {}
  ngOnInit() {
    this.teamMemberService.getTeamMembers().subscribe((data) => {
        this.teamMembers = data.map(member => {
            member.image = this.imageService.getImageUrl(member.image);
            return member;
        });
    });
}


  openNew() {
    this.teamMember = {};
    this.submitted = false;
    this.teamMemberDialog = true;
  }

  deleteSelectedTeamMembers() {
    if (this.selectedTeamMembers.length === 1) {
      this.deleteTeamMember(this.selectedTeamMembers[0]);
    } else {
      this.deleteTeamMembersDialog = true;
    }
  }
  // Add the following method for deleting multiple team members
confirmDeleteSelected() {
  this.deleteTeamMembersDialog = false;
  const selectedIds = this.selectedTeamMembers.map(member => member._id);

  console.log(selectedIds)
  this.teamMemberService.deleteTeamMembers(selectedIds).subscribe(() => {
    this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Team Members Deleted', life: 3000 });
    this.teamMembers = this.teamMembers.filter((val) => !this.selectedTeamMembers.includes(val));
    this.selectedTeamMembers = [];
  });
}
  editTeamMember(teamMember: TeamMember) {
    this.teamMember = { ...teamMember };
    this.teamMemberDialog = true;
  }

  deleteTeamMember(teamMember: TeamMember) {
    this.deleteTeamMemberDialog = true;
    this.teamMember = { ...teamMember };
  }


  confirmDelete() {
    this.deleteTeamMemberDialog = false;
    this.teamMembers = this.teamMembers.filter((val) => val._id !== this.teamMember._id);
    this.teamMemberService.deleteTeamMember(this.teamMember._id).subscribe(() => {
      this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Team Member Deleted', life: 3000 });
      this.teamMembers = this.teamMembers.filter((val) => val._id !== this.teamMember._id);
      this.teamMember = {};
    });
  }

  hideDialog() {
    this.teamMemberDialog = false;
    this.submitted = false;
  }

  saveTeamMember() {
    this.submitted = true;

    if (this.teamMember.name?.trim() && this.teamMember.position?.trim()) {
      const formData = new FormData();

      // Append image data if available
      if (this.teamMember.image) {
        // Convert the base64 image data to a Blob
        const blob = this.dataURItoBlob(this.teamMember.image);

        // Append the Blob to the FormData
        formData.append('image', blob, 'image.png');
      }

      // Append other data properties
      formData.append('name', this.teamMember.name);
      formData.append('position', this.teamMember.position);

      // Determine whether to add or update based on the existence of _id
      if (this.teamMember._id) {
        // Update existing team member
        this.teamMemberService.updateTeamMemberWithImage(this.teamMember._id, formData).subscribe(() => {
          this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Team Member Updated', life: 3000 });
        });
      } else {
        // Add new team member
        this.teamMemberService.addTeamMemberWithImage(formData).subscribe(() => {
          this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Team Member Created', life: 3000 });
        });
      }

      this.teamMembers = [...this.teamMembers];
      this.teamMemberDialog = false;
      this.teamMember = {};
    }
  }

  // Convert a data URI to a Blob
  dataURItoBlob(dataURI: string): Blob {
    const byteString = atob(dataURI.split(',')[1]);
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) {
      ia[i] = byteString.charCodeAt(i);
    }
    return new Blob([ab], { type: 'image/png' });
  }





  findIndexById(id: string): number {
    let index = -1;
    for (let i = 0; i < this.teamMembers.length; i++) {
      if (this.teamMembers[i]._id === id) {
        index = i;
        break;
      }
    }
    return index;
  }

  createId(): string {
    let id = '';
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    for (let i = 0; i < 5; i++) {
      id += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return id;
  }

  onGlobalFilter(table: Table, event: Event) {
    table.filterGlobal((event.target as HTMLInputElement).value, 'contains');
  }

  handleImageChange(event: any) {
    const file = event.target.files[0];

    // You can handle the file, for example, by uploading it to the server
    // or displaying a preview. Assuming you want to display a preview:

    if (file) {
        const reader = new FileReader();
        reader.onload = (e: any) => {
            // Update the team member's image property with the base64 data URL
            this.teamMember.image = e.target.result;
        };
        reader.readAsDataURL(file);
    }
}
}
<div class="grid">
    <div class="col-12">
      <div class="card px-6 py-6">
        <p-toast></p-toast>
        <p-toolbar styleClass="mb-4">
          <ng-template pTemplate="left">
            <div class="my-2">
              <button pButton pRipple label="New" icon="pi pi-plus" class="p-button-success mr-2" (click)="openNew()"></button>
              <button pButton pRipple label="Delete" icon="pi pi-trash" class="p-button-danger" (click)="deleteSelectedTeamMembers()"
                [disabled]="!selectedTeamMembers || !selectedTeamMembers.length"></button>
            </div>
          </ng-template>

          <ng-template pTemplate="right">
            <p-fileUpload mode="basic" accept="image/*" [maxFileSize]="1000000" label="Import" chooseLabel="Import" class="mr-2 inline-block"></p-fileUpload>
            <button pButton pRipple label="Export" icon="pi pi-upload" class="p-button-help" (click)="dt.exportCSV()"></button>
          </ng-template>
        </p-toolbar>

        <p-table #dt [value]="teamMembers" [columns]="cols" responsiveLayout="scroll" [rows]="10"
          [globalFilterFields]="['name','position','image']" [paginator]="true" [rowsPerPageOptions]="[10,20,30]"
          [showCurrentPageReport]="true" currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
          [(selection)]="selectedTeamMembers" selectionMode="multiple" [rowHover]="true" dataKey="_id">
          <ng-template pTemplate="caption">
            <div class="flex flex-column md:flex-row md:justify-content-between md:align-items-center">
              <h5 class="m-0">Manage Team Members</h5>
              <span class="block mt-2 md:mt-0 p-input-icon-left">
                <i class="pi pi-search"></i>
                <input pInputText type="text" (input)="onGlobalFilter(dt, $event)" placeholder="Search..." class="w-full sm:w-auto"/>
              </span>
            </div>
          </ng-template>
          <ng-template pTemplate="header">
            <tr>
              <th style="width: 3rem">
                <p-tableHeaderCheckbox></p-tableHeaderCheckbox>
              </th>
              <th pSortableColumn="name">Name <p-sortIcon field="name"></p-sortIcon></th>
              <th pSortableColumn="position">Position <p-sortIcon field="position"></p-sortIcon></th>
              <th>Image</th>
              <th></th>
            </tr>
          </ng-template>
          <ng-template pTemplate="body" let-teamMember>
            <tr>
              <td>
                <p-tableCheckbox [value]="teamMember"></p-tableCheckbox>
              </td>
              <td style="width: 30%;">
                <span class="p-column-title">Name</span>
                {{teamMember.name}}
              </td>
              <td style="width: 30%;">
                <span class="p-column-title">Position</span>
                {{teamMember.position}}
              </td>
<!-- Inside ng-template pTemplate="body" let-teamMember -->
<td style="width: 30%;">
    <span class="p-column-title">Image</span>
    <img [src]="teamMember.image" [alt]="teamMember.name" width="80" class="shadow-4" />
</td>


              <td>
                <div class="flex">
                  <button pButton pRipple icon="pi pi-pencil" class="p-button-rounded p-button-success mr-2" (click)="editTeamMember(teamMember)"></button>
                  <button pButton pRipple icon="pi pi-trash" class="p-button-rounded p-button-warning" (click)="deleteTeamMember(teamMember)"></button>
                </div>
              </td>
            </tr>
          </ng-template>
        </p-table>
      </div>
<!-- Team Member Dialog -->
<p-dialog [(visible)]="teamMemberDialog" [style]="{width: '450px'}" header="Team Member Details" [modal]="true" class="p-fluid">
    <ng-template pTemplate="content">
      <div class="field">
        <label for="name">Name</label>
        <input type="text" pInputText id="name" [(ngModel)]="teamMember.name" required autofocus [ngClass]="{'ng-invalid ng-dirty' : submitted && !teamMember.name}"/>
        <small class="ng-dirty ng-invalid" *ngIf="submitted && !teamMember.name">Name is required.</small>
      </div>
      <div class="field">
        <label for="position">Position</label>
        <input type="text" pInputText id="position" [(ngModel)]="teamMember.position" required [ngClass]="{'ng-invalid ng-dirty' : submitted && !teamMember.position}"/>
        <small class="ng-dirty ng-invalid" *ngIf="submitted && !teamMember.position">Position is required.</small>
      </div>
      <div class="field">
        <label for="image">Image</label>
        <input type="file" pInputText id="image" (change)="handleImageChange($event)" />
      </div>

    </ng-template>

    <ng-template pTemplate="footer">
      <button pButton pRipple label="Cancel" icon="pi pi-times" class="p-button-text" (click)="hideDialog()"></button>
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" (click)="saveTeamMember()"></button>
    </ng-template>
  </p-dialog>

  <!-- Delete Team Member Dialog -->
  <p-dialog [(visible)]="deleteTeamMemberDialog" header="Confirm" [modal]="true" [style]="{width:'450px'}">
    <div class="flex align-items-center justify-content-center">
      <i class="pi pi-exclamation-triangle mr-3" style="font-size: 2rem"></i>
      <span *ngIf="teamMember">Are you sure you want to delete <b>{{teamMember.name}}</b>?</span>
    </div>
    <ng-template pTemplate="footer">
      <button pButton pRipple icon="pi pi-times" class="p-button-text" label="No" (click)="deleteTeamMemberDialog = false"></button>
      <button pButton pRipple icon="pi pi-check" class="p-button-text" label="Yes" (click)="confirmDelete()"></button>
    </ng-template>
  </p-dialog>

  <!-- Delete Team Members Dialog -->
  <p-dialog [(visible)]="deleteTeamMembersDialog" header="Confirm" [modal]="true" [style]="{width:'450px'}">
    <div class="flex align-items-center justify-content-center">
      <i class="pi pi-exclamation-triangle mr-3" style="font-size: 2rem"></i>
      <span>Are you sure you want to delete selected team members?</span>
    </div>
    <ng-template pTemplate="footer">
      <button pButton pRipple icon="pi pi-times" class="p-button-text" label="No" (click)="deleteTeamMembersDialog = false"></button>
      <button pButton pRipple icon="pi pi-check" class="p-button-text" label="Yes" (click)="confirmDeleteSelected()"></button>
    </ng-template>
  </p-dialog>

    </div>
</div>
