import { Injectable } from "@angular/core";

@Injectable({
    providedIn:'root',
})
export class ImageService {
    // Assuming your backend URL for serving images
    private imageEndpoint = 'http://localhost:3002/uploads'; // Add the endpoint for serving images
  
    constructor() {}
  
    getImageUrl(imageFileName: string): string {
      return `${this.imageEndpoint}${imageFileName}`;
    }
  }
