import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthStudentService } from 'src/app/demo/service/auth-student.service';
import { AuthTeacherService } from 'src/app/demo/service/auth-teacher.service';

@Component({
  selector: 'app-auth-dialog',
  templateUrl: './auth-dialog.component.html',
  styleUrls: ['./auth-dialog.component.scss']
})
export class AuthDialogComponent implements OnInit {
// Inside your component class
// Inside your component class
activeType: 'Login' | 'Register' = 'Login';
activeRole: 'Student' | 'Teacher' = 'Student';
  studentLoginForm: FormGroup;
  teacherLoginForm: FormGroup;
  studentRegisterForm: FormGroup;
  teacherRegisterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authStudentService: AuthStudentService,
    private authTeacherService: AuthTeacherService
  ) {}

  ngOnInit(): void {
    this.studentLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherLoginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.studentRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.teacherRegisterForm = this.fb.group({
      name: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      specialization: [''] // Optional field
    });

  }

  toggleType(type: 'Login' | 'Register') {
    this.activeType = type;
  }

  toggleRole(role: 'Student' | 'Teacher') {
    this.activeRole = role;
  }

  getFormGroup(): FormGroup {
    return this.activeType === 'Login' ? this.loginForm : this.registerForm;
  }

  submitForm() {
    if (this.activeType === 'Login') {
      // Handle login submission
    } else {
      // Handle registration submission
    }
  }
  studentLogin() {
    if (this.studentLoginForm.valid) {
      this.authStudentService.studentLogin(this.studentLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherLogin() {
    if (this.teacherLoginForm.valid) {
      this.authTeacherService.teacherLogin(this.teacherLoginForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  studentRegister() {
    if (this.studentRegisterForm.valid) {
      this.authStudentService.studentRegister(this.studentRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }

  teacherRegister() {
    if (this.teacherRegisterForm.valid) {
      this.authTeacherService.teacherRegister(this.teacherRegisterForm.value).subscribe(
        (response) => {
          // Handle success
        },
        (error) => {
          // Handle error
        }
      );
    }
  }
}
<div class="auth-dialog bg-white rounded-lg p-8 max-w-md w-full">
  <!-- Switch between Login/Register -->
  <div class="flex justify-center mb-4">
    <button *ngFor="let type of ['Login', 'Register']" class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="activeType === type" (click)="toggleType(type)">{{ type }}</button>
  </div>

  <!-- Switch between Student/Teacher -->
  <div *ngIf="activeType">
    <div class="flex justify-center mb-4">
      <button *ngFor="let role of ['Student', 'Teacher']" class="mr-2 py-2 px-4 rounded hover:bg-gray-200 focus:outline-none" [class.bg-gray-200]="activeRole === role" (click)="toggleRole(role)">{{ role }}</button>
    </div>

    <!-- Login/Register Form -->
    <div *ngIf="activeType && activeRole">
      <h2 class="text-2xl font-bold mb-4">{{ activeType }} {{ activeRole }} {{ activeType === 'Register' ? 'Register' : 'Login' }}</h2>
      <form [formGroup]="getFormGroup()" (ngSubmit)="submitForm()">
        <!-- Form Fields -->
      </form>
    </div>
  </div>
</div>
