import { Component, ElementRef, HostListener, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { LayoutService } from 'src/app/layout/service/app.layout.service';
import { trigger, state, style, animate, transition } from '@angular/animations';
const fadeStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const fadeStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const leftToRightStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const leftToRightStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const fadeDownStyle1 = style({
  opacity: 1,
  transform: 'translateY(0)'
});

const fadeDownStyle2 = style({
  opacity: 0,
  transform: 'translateY(-100%)'
});
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls:['./landing.component.scss'],
})
export class LandingComponent {
  @ViewChild('s1', { static: true }) section1: ElementRef;

  isNavbarGray = false;
  isResponsiveMenuGray: boolean = false;
  animationState = 'hide';
  activeSection: string | null = null;
  sectionOffsets: { [key: string]: number } = {};

  constructor(public layoutService: LayoutService, public router: Router,public el: ElementRef,private renderer: Renderer2) { }
  ngAfterViewInit() {
    // Populate the sections array with ElementRef references
    this.sections = [
      this.section1.nativeElement,
      // Add other sections as needed
    ];
  }

@HostListener('window:scroll', ['$event'])
checkScroll() {
  this.isNavbarGray = window.scrollY > 100; // Adjust the scroll position threshold

  const scrollPosition = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;

  // Iterate through sections and apply animation classes
  this.sections.forEach((section, index) => {
    const offset = section.nativeElement.offsetTop;
    const threshold = offset - window.innerHeight * 0.8; // Adjust the threshold as needed

    // Toggle animation classes based on scroll position
    if (scrollPosition > threshold) {
      this.renderer.addClass(section.nativeElement, 'animate__animated');
      this.renderer.addClass(section.nativeElement, 'animate__fadeInUp');
      // Add other animation classes as needed
    } else {
      this.renderer.removeClass(section.nativeElement, 'animate__animated');
      this.renderer.removeClass(section.nativeElement, 'animate__fadeInUp');
      // Remove other animation classes as needed
    }
  });
}

}
