import { Component, HostListener, ElementRef } from '@angular/core';
import { Router } from '@angular/router';

@Component({
    selector: 'app-landing',
    templateUrl: './landing.component.html',
    styleUrls: ['./landing.component.scss']
})
export class LandingComponent {
  isShrunk: boolean = false;

  constructor(public layoutService: LayoutService,,public router: Router, private el: ElementRef) { }

  @HostListener('window:scroll', ['$event'])
  onScroll() {
    // Update isShrunk based on the scroll position
    this.isShrunk = window.scrollY > 80;
  }

  scrollToSection(fragment: string): void {
    const element = this.el.nativeElement.querySelector(`[id=${fragment}]`);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  }
}
