import { Component, OnInit } from '@angular/core';
import { Project } from 'src/app/demo/api/project';
import { MessageService } from 'primeng/api';
import { Table } from 'primeng/table';
import { ImageService } from 'src/app/demo/service/image.service';
import { ProjectService } from 'src/app/demo/service/project.service';

@Component({
  templateUrl: './project.component.html',
  providers: [MessageService, ProjectService],
})
export class ProjectComponent implements OnInit {
  projects: Project[] = [];
  project: Project | null = null;
  projectDialog: boolean = false;
  deleteProjectDialog: boolean = false;
  deleteProjectsDialog: boolean = false;
  selectedProjects: Project[] = [];
  submitted: boolean = false;

  cols: any[] = [
    { field: 'title', header: 'Title' },
    { field: 'description', header: 'Description' },
    { field: 'image', header: 'Image' },
  ];

  constructor(private projectService: ProjectService, private messageService: MessageService, private imageService: ImageService) { }

  ngOnInit() {
    this.projectService.getProjects().subscribe((data) => {
      this.projects = data.map(project => {
        project.image = this.imageService.getImageUrl(project.image);
        return project;
      });
    });
  }

  openNew() {
    this.project = {};
    this.submitted = false;
    this.projectDialog = true;
  }

  deleteSelectedProjects() {
    if (this.selectedProjects.length === 1) {
      this.deleteProject(this.selectedProjects[0]);
    } else {
      this.deleteProjectsDialog = true;
    }
  }

  confirmDeleteSelected() {
    this.deleteProjectsDialog = false;
    const selectedIds = this.selectedProjects.map(project => project._id);

    console.log(selectedIds);
    this.projectService.deleteProjects(selectedIds).subscribe(
      () => {
        this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Projects Deleted', life: 3000 });
        this.projects = this.projects.filter((val) => !this.selectedProjects.includes(val));
        this.selectedProjects = [];
      },
      (error) => {
        console.error(error);
        // Handle error, display an error message, etc.
      }
    );
  }

  editProject(project: Project) {
    this.project = { ...project };
    this.projectDialog = true;
  }

  deleteProject(project: Project) {
    this.deleteProjectDialog = true;
    this.project = { ...project };
  }

  confirmDelete() {
    this.deleteProjectDialog = false;
    this.projects = this.projects.filter((val) => val._id !== this.project._id);
    this.projectService.deleteProject(this.project._id).subscribe(() => {
      this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Project Deleted', life: 3000 });
      this.projects = this.projects.filter((val) => val._id !== this.project._id);
      this.project = {};
    });
  }

  hideDialog() {
    this.projectDialog = false;
    this.submitted = false;
  }

  saveProject() {
    this.submitted = true;

    if (this.project.title?.trim() && this.project.description?.trim()) {
      const formData = new FormData();

      // Append image data if available
      if (this.project.image) {
        // Convert the base64 image data to a Blob
        const blob = this.dataURItoBlob(this.project.image);

        // Append the Blob to the FormData
        formData.append('image', blob, 'image.png');
      }

      // Append other data properties
      formData.append('title', this.project.title);
      formData.append('description', this.project.description);

      // Determine whether to add or update based on the existence of _id
      if (this.project._id) {
        // Update existing project
        this.projectService.updateProjectWithImage(this.project._id, formData).subscribe(() => {
          this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Project Updated', life: 3000 });
        });
      } else {
        // Add new project
        this.projectService.addProjectWithImage(formData).subscribe(() => {
          this.messageService.add({ severity: 'success', summary: 'Successful', detail: 'Project Created', life: 3000 });
        });
      }

      this.projects = [...this.projects];
      this.projectDialog = false;
      this.project = {};
    }
  }

  dataURItoBlob(dataURI: string): Blob {
    const byteString = atob(dataURI.split(',')[1]);
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) {
      ia[i] = byteString.charCodeAt(i);
    }
    return new Blob([ab], { type: 'image/png' });
  }

  findIndexById(id: string): number {
    let index = -1;
    for (let i = 0; i < this.projects.length; i++) {
      if (this.projects[i]._id === id) {
        index = i;
        break;
      }
    }
    return index;
  }

  createId(): string {
    let id = '';
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    for (let i = 0; i < 5; i++) {
      id += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return id;
  }

  onGlobalFilter(table: Table, event: Event) {
    table.filterGlobal((event.target as HTMLInputElement).value, 'contains');
  }

  handleImageChange(event: any) {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.project.image = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  }
}
<div class="grid">
    <div class="col-12">
      <div class="card px-6 py-6">
        <p-toast></p-toast>
        <p-toolbar styleClass="mb-4">
          <ng-template pTemplate="left">
            <div class="my-2">
              <button pButton pRipple label="New" icon="pi pi-plus" class="p-button-success mr-2" (click)="openNew()"></button>
              <button pButton pRipple label="Delete" icon="pi pi-trash" class="p-button-danger" (click)="deleteSelectedProjects()" [disabled]="!selectedProjects || !selectedProjects.length"></button>
            </div>
          </ng-template>

          <ng-template pTemplate="right">
            <p-fileUpload mode="basic" accept="image/*" [maxFileSize]="1000000" label="Import" chooseLabel="Import" class="mr-2 inline-block"></p-fileUpload>
            <button pButton pRipple label="Export" icon="pi pi-upload" class="p-button-help" (click)="dt.exportCSV()"></button>
          </ng-template>
        </p-toolbar>

            <p-table #dt [value]="projects" [columns]="cols" responsiveLayout="scroll" [rows]="10"
                [globalFilterFields]="['title', 'description', 'image']" [paginator]="true"
                [rowsPerPageOptions]="[10,20,30]" [showCurrentPageReport]="true"
                currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
                [(selection)]="selectedProjects" selectionMode="multiple" [rowHover]="true" dataKey="_id">
                <ng-template pTemplate="caption">
                    <div
                        class="flex flex-column md:flex-row md:justify-content-between md:align-items-center">
                        <h5 class="m-0">Manage Projects</h5>
                        <span class="block mt-2 md:mt-0 p-input-icon-left">
                            <i class="pi pi-search"></i>
                            <input pInputText type="text" (input)="onGlobalFilter(dt, $event)"
                                placeholder="Search..." class="w-full sm:w-auto" />
                        </span>
                    </div>
                </ng-template>
                <ng-template pTemplate="header">
                    <tr>
                        <th style="width: 3rem">
                            <p-tableHeaderCheckbox></p-tableHeaderCheckbox>
                        </th>
                        <th pSortableColumn="title">Title <p-sortIcon field="title"></p-sortIcon></th>
                        <th pSortableColumn="description">Description <p-sortIcon field="description"></p-sortIcon></th>
                        <th>Image</th>
                        <th></th>
                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-project>
                    <tr>
                        <td>
                            <p-tableCheckbox [value]="project"></p-tableCheckbox>
                        </td>
                        <td style="width: 30%;">
                            <span class="p-column-title">Title</span>
                            {{project.title}}
                        </td>
                        <td style="width: 30%;">
                            <span class="p-column-title">Description</span>
                            {{project.description}}
                        </td>
                        <td style="width: 30%;">
                            <span class="p-column-title">Image</span>
                            <img [src]="project.image" [alt]="project.title" width="80" class="shadow-4" />
                        </td>
                        <td>
                            <div class="flex">
                                <button pButton pRipple icon="pi pi-pencil"
                                    class="p-button-rounded p-button-success mr-2"
                                    (click)="editProject(project)"></button>
                                <button pButton pRipple icon="pi pi-trash"
                                    class="p-button-rounded p-button-warning"
                                    (click)="deleteProject(project)"></button>
                            </div>
                        </td>
                    </tr>
                </ng-template>
            </p-table>
        </div>
        <!-- Project Dialog -->
      <!-- Project Dialog -->
      <p-dialog [(visible)]="projectDialog" [style]="{width: '450px'}" header="Project Details" [modal]="true" class="p-fluid">
        <ng-template pTemplate="content">
          <div class="field">
            <label for="title">Title</label>
            <input type="text" pInputText id="title" [(ngModel)]="teamMember.name" required autofocus [ngClass]="{'ng-invalid ng-dirty' : submitted && !teamMember.name}"/>
            <small class="ng-dirty ng-invalid" *ngIf="submitted && !teamMember.name">Name is required.</small>
          </div>
          <div class="field">
            <label for="position">Position</label>
            <input type="text" pInputText id="position" [(ngModel)]="teamMember.position" required [ngClass]="{'ng-invalid ng-dirty' : submitted && !teamMember.position}"/>
            <small class="ng-dirty ng-invalid" *ngIf="submitted && !teamMember.position">Position is required.</small>
          </div>
          <div class="field">
            <label for="image">Image</label>
            <input type="file" pInputText id="image" (change)="handleImageChange($event)" />
          </div>

        </ng-template>

        <ng-template pTemplate="footer">
          <button pButton pRipple label="Cancel" icon="pi pi-times" class="p-button-text" (click)="hideDialog()"></button>
          <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" (click)="saveTeamMember()"></button>
        </ng-template>
      </p-dialog>
        <!-- Delete Project Dialog -->
        <p-dialog [(visible)]="deleteProjectDialog" header="Confirm" [modal]="true" [style]="{width:'450px'}">
            <div class="flex align-items-center justify-content-center">
                <i class="pi pi-exclamation-triangle mr-3" style="font-size: 2rem"></i>
                <span *ngIf="project">Are you sure you want to delete <b>{{project.title}}</b>?</span>
            </div>
            <ng-template pTemplate="footer">
                <button pButton pRipple icon="pi pi-times" class="p-button-text" label="No"
                    (click)="deleteProjectDialog = false"></button>
                <button pButton pRipple icon="pi pi-check" class="p-button-text" label="Yes"
                    (click)="confirmDelete()"></button>
            </ng-template>
        </p-dialog>

        <!-- Delete Projects Dialog -->
        <p-dialog [(visible)]="deleteProjectsDialog" header="Confirm" [modal]="true" [style]="{width:'450px'}">
            <div class="flex align-items-center justify-content-center">
                <i class="pi pi-exclamation-triangle mr-3" style="font-size: 2rem"></i>
                <span>Are you sure you want to delete selected projects?</span>
            </div>
            <ng-template pTemplate="footer">
                <button pButton pRipple icon="pi pi-times" class="p-button-text" label="No"
                    (click)="deleteProjectsDialog = false"></button>
                <button pButton pRipple icon="pi pi-check" class="p-button-text" label="Yes"
                    (click)="confirmDeleteSelected()"></button>
            </ng-template>
        </p-dialog>
    </div>
</div>
