import { Component, ElementRef, HostListener } from '@angular/core';
import { Router } from '@angular/router';
import { LayoutService } from 'src/app/layout/service/app.layout.service';
import { trigger, state, style, animate, transition } from '@angular/animations';
const fadeStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const fadeStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const leftToRightStyle1 = style({
  opacity: 1,
  transform: 'translateX(0)'
});

const leftToRightStyle2 = style({
  opacity: 0,
  transform: 'translateX(-100%)'
});

const fadeDownStyle1 = style({
  opacity: 1,
  transform: 'translateY(0)'
});

const fadeDownStyle2 = style({
  opacity: 0,
  transform: 'translateY(-100%)'
});
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls:['./landing.component.scss'],
})
export class LandingComponent {
  isNavbarGray = false;
  isResponsiveMenuGray: boolean = false;
  animation = 'hide';

  constructor(public layoutService: LayoutService, public router: Router,public el: ElementRef) { }


  toggleNavbarColor() {
    this.isNavbarGray = true;
}
@HostListener('window:scroll', ['$event'])
checkScroll() {
  this.isNavbarGray = window.scrollY > 100; // Adjust the scroll position threshold

  const componentPosition = this.el.nativeElement.offsetTop;

  // For the  animation
  if (window.window.scrollY >= componentPosition - 10) {
    this.animation = 'show';
  } 


}

}
